INSERT INTO `wp3_posts` VALUES("267", "1", "2020-05-04 09:00:26", "2020-05-04 07:00:26", "<!-- wp:image {\"id\":51,\"width\":665,\"height\":556,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large is-resized\"><img src=\"http://host/DWWM-Mederic/projet%20conan/conan/wp-content/uploads/2020/04/boite3D_conan_applat-1024x857-600x502-1.jpg\" alt=\"\" class=\"wp-image-51\" width=\"665\" height=\"556\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"className\":\"intro\"} -->\n<p class=\"intro\">Vivez les aventures de Conan, Shevatas le voleur ou Hadrathus le prêtre. Combattez des monstres, des nécromants ou des Pictes menés par Zogar Sag. Voyagez aux côtés de Bêlit, la Reine de la Côte Noire, et son équipage de pirates. Menez de grandes batailles dans des auberges emplies de gardes ou des châteaux en ruine.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":33.33} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33.33%\"><!-- wp:heading {\"level\":3} -->\n<h3>Contenu :</h3>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>74 figurines</li><li>1 Livre de Skelos</li><li>2 plateau recto-verso</li><li>45 tuiles</li><li>55 cartes (42x63mm)</li><li>9 dés gravés</li><li>4 fiches de Héros</li><li>148&nbsp;pions</li><li>75 gemmes</li><li>25 bases</li><li>4 aides de jeu</li><li>1 piste compte-tour</li><li>1 livret de règles des Héros</li><li>1 livret de règles de l’Overlord</li></ul>\n<!-- /wp:list --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":66.66} -->\n<div class=\"wp-block-column\" style=\"flex-basis:66.66%\"><!-- wp:image {\"id\":50,\"width\":426,\"height\":320,\"sizeSlug\":\"large\"} -->\n<figure class=\"wp-block-image size-large is-resized\"><img src=\"http://host/DWWM-Mederic/projet%20conan/conan/wp-content/uploads/2020/04/contenu.jpg\" alt=\"\" class=\"wp-image-50\" width=\"426\" height=\"320\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->", "Boite", "", "inherit", "closed", "closed", "", "49-revision-v1", "", "", "2020-05-04 09:00:26", "2020-05-04 07:00:26", "", "49", "http://host/DWWM-Mederic/projet%20conan/conan/index.php/2020/05/04/49-revision-v1/", "0", "revision", "", "0");
